mapper.insert:

	SELECT
		(case COUNT(*)
		when 0 then'disposal_1'
		else CONCAT('disposal_',MAX(CAST(SUBSTRING(animal_disposal_code,10) AS DECIMAL))+1) 
		END)AS animal_disposal_code
	FROM
		tb_animal_disposal

INSERT INTO tb_animal_disposal
(animal_disposal_code
, animal_insert_code
, animal_disposal_result
, animal_disposal_pasture_info
, animal_disposal_die_info
, animal_disposal_date
, staff_code
, adoptee_code
, center_return_code)
VALUES (#{animalDisposalCode}
		, #{animalCneter.animalInsertCode}
		, #{animalDisposalResult}
		, #{animalDisposalPastureInfo}
		, #{animalDisposalDieInfo}
		, NOW()
		, #{staff.staffCode}
		, #{adoptee.adopteeCode}
		, #{centerReturn.centerReturnCode})

mapper.select:
SELECT * FROM tb_animal_disposal AS d LEFT JOIN tb_animal_insert AS i ON d.animal_insert_code = i.animal_insert_code LEFT JOIN tb_staff AS s ON d.staff_code = s.staff_code LEFT JOIN tb_adoptee AS a ON d.adoptee_code = a.adoptee_code LEFT JOIN tb_center_return AS c ON d.center_return_code = c.center_return_code LEFT JOIN tb_member AS m ON a.member_id = m.member_id
d.animal_insert_code = #{send_code}
ORDER BY d.animal_disposal_date DESC
